-- ----------------------------
-- Table structure for `content`
-- ----------------------------
DROP TABLE IF EXISTS `content`;
CREATE TABLE `content` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `channel_id` int(11) NOT NULL,
  `title` varchar(200) NOT NULL,
  `brief` varchar(200) DEFAULT NULL,
  `content` mediumtext,
  `creator` varchar(20) NOT NULL,
  `modifier` varchar(20) NOT NULL,
  `create_date` datetime NOT NULL,
  `modify_date` datetime NOT NULL,
  `file_upload` varchar(100) DEFAULT NULL,
  `viewer` varchar(100) DEFAULT NULL,
  `image` varchar(100) DEFAULT NULL COMMENT '题标图片',
  `top` int(1) DEFAULT '0' COMMENT '置顶标志位',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=272 DEFAULT CHARSET=utf8;

